import Object = require('nashorn/java/lang/Object');
import TObjectHashingStrategy = require('nashorn/gnu/trove/TObjectHashingStrategy');

declare class EqualityPolicy<T> extends Object {
	static IDENTITY : EqualityPolicy<any>;
	static CANONICAL : EqualityPolicy<any>;
	getHashCode(arg1 : T) : number;
	isEqual(arg1 : T, arg2 : T) : boolean;
}


declare module EqualityPolicy {
	class ByHashingStrategy<T> {
		constructor(arg1 : TObjectHashingStrategy<T>);
	}


	interface ByHashingStrategy<T> extends EqualityPolicy<T> {}}

export = EqualityPolicy
