import Object = require('nashorn/java/lang/Object');
import Ref = require('nashorn/com/intellij/openapi/util/Ref');

declare class FlyweightCapableTreeStructure<T> extends Object {
	root : T;
	getRoot() : T;
	getParent(arg1 : T) : T;
	prepareForGetChildren(arg1 : T) : T;
	getChildren(arg1 : T, arg2 : Ref<T[]>) : number;
	disposeChildren(arg1 : T[], arg2 : number) : void;
}

export = FlyweightCapableTreeStructure
