import FileType = require('nashorn/com/intellij/openapi/fileTypes/FileType');
import VirtualFile = require('nashorn/com/intellij/openapi/vfs/VirtualFile');
import CharSequence = require('nashorn/java/lang/CharSequence');
import Project = require('nashorn/com/intellij/openapi/project/Project');
import PsiFile = require('nashorn/com/intellij/psi/PsiFile');
import UserDataHolder = require('nashorn/com/intellij/openapi/util/UserDataHolder');

declare class FileContent {
	fileType : FileType;
	file : VirtualFile;
	fileName : string;
	content : number[];
	contentAsText : CharSequence;
	project : Project;
	psiFile : PsiFile;
	getFileType() : FileType;
	getFile() : VirtualFile;
	getFileName() : string;
	getContent() : number[];
	getContentAsText() : CharSequence;
	getProject() : Project;
	getPsiFile() : PsiFile;
}


declare interface FileContent extends UserDataHolder {}
export = FileContent
