import Topic = require('nashorn/com/intellij/util/messages/Topic');
import MessageHandler = require('nashorn/com/intellij/util/messages/MessageHandler');
import Disposable = require('nashorn/com/intellij/openapi/Disposable');

declare class MessageBusConnection {
	subscribe<L>(arg1 : Topic<L>, arg2 : L) : void;
	subscribe<L>(arg1 : Topic<L>) : void;
	setDefaultHandler(arg1 : MessageHandler) : void;
	deliverImmediately() : void;
	disconnect() : void;
}


declare interface MessageBusConnection extends Disposable {}
export = MessageBusConnection
