import Object = require('nashorn/java/lang/Object');
import Class = require('nashorn/java/lang/Class');
import Enum = require('nashorn/java/lang/Enum');

declare class Topic<L> extends Object {
	displayName : string;
	listenerClass : Class<L>;
	broadcastDirection : Topic.BroadcastDirection;
	constructor(arg1 : string, arg2 : Class<L>);
	constructor(arg1 : string, arg2 : Class<L>, arg3 : Topic.BroadcastDirection);
	getDisplayName() : string;
	getListenerClass() : Class<L>;
	static create<L>(arg1 : string, arg2 : Class<L>) : Topic<L>;
	static create<L>(arg1 : string, arg2 : Class<L>, arg3 : Topic.BroadcastDirection) : Topic<L>;
	getBroadcastDirection() : Topic.BroadcastDirection;
}


declare module Topic {
	class BroadcastDirection {
		static TO_CHILDREN : Topic.BroadcastDirection;
		static NONE : Topic.BroadcastDirection;
		static TO_PARENT : Topic.BroadcastDirection;
	}


	interface BroadcastDirection extends Enum<Topic.BroadcastDirection> {}}

export = Topic
