import Object = require('nashorn/java/lang/Object');
import List = require('nashorn/java/util/List');
import XmlElementDescriptor = require('nashorn/com/intellij/xml/XmlElementDescriptor');
import Enum = require('nashorn/java/lang/Enum');

declare class XmlElementsGroup extends Object {
	minOccurs : number;
	maxOccurs : number;
	groupType : XmlElementsGroup.Type;
	parentGroup : XmlElementsGroup;
	subGroups : List<XmlElementsGroup>;
	leafDescriptor : XmlElementDescriptor;
	getMinOccurs() : number;
	getMaxOccurs() : number;
	getGroupType() : XmlElementsGroup.Type;
	getParentGroup() : XmlElementsGroup;
	getSubGroups() : List<XmlElementsGroup>;
	getLeafDescriptor() : XmlElementDescriptor;
}


declare module XmlElementsGroup {
	class Type {
		static SEQUENCE : XmlElementsGroup.Type;
		static CHOICE : XmlElementsGroup.Type;
		static ALL : XmlElementsGroup.Type;
		static GROUP : XmlElementsGroup.Type;
		static LEAF : XmlElementsGroup.Type;
	}


	interface Type extends Enum<XmlElementsGroup.Type> {}}

export = XmlElementsGroup
