import ImageCapabilities = require('nashorn/java/awt/ImageCapabilities');
import Cloneable = require('nashorn/java/lang/Cloneable');
import AttributeValue = require('nashorn/java/awt/AttributeValue');

declare class BufferCapabilities {
	frontBufferCapabilities : ImageCapabilities;
	backBufferCapabilities : ImageCapabilities;
	pageFlipping : boolean;
	flipContents : BufferCapabilities.FlipContents;
	fullScreenRequired : boolean;
	multiBufferAvailable : boolean;
	constructor(arg1 : ImageCapabilities, arg2 : ImageCapabilities, arg3 : BufferCapabilities.FlipContents);
	getFrontBufferCapabilities() : ImageCapabilities;
	getBackBufferCapabilities() : ImageCapabilities;
	isPageFlipping() : boolean;
	getFlipContents() : BufferCapabilities.FlipContents;
	isFullScreenRequired() : boolean;
	isMultiBufferAvailable() : boolean;
}


declare interface BufferCapabilities extends Cloneable {}

declare module BufferCapabilities {
	class FlipContents {
		static UNDEFINED : BufferCapabilities.FlipContents;
		static BACKGROUND : BufferCapabilities.FlipContents;
		static PRIOR : BufferCapabilities.FlipContents;
		static COPIED : BufferCapabilities.FlipContents;
	}


	interface FlipContents extends AttributeValue {}}

export = BufferCapabilities
