import Object = require('nashorn/java/lang/Object');
import File = require('nashorn/java/io/File');
import URI = require('nashorn/java/net/URI');
import Enum = require('nashorn/java/lang/Enum');

declare class Desktop extends Object {
	static desktop : Desktop;
	static desktopSupported : boolean;
	static getDesktop() : Desktop;
	static isDesktopSupported() : boolean;
	isSupported(arg1 : Desktop.Action) : boolean;
	static checkFileValidation(arg1 : File) : void;
	checkActionSupport(arg1 : Desktop.Action) : void;
	checkAWTPermission() : void;
	open(arg1 : File) : void;
	edit(arg1 : File) : void;
	print(arg1 : File) : void;
	browse(arg1 : URI) : void;
	mail() : void;
	mail(arg1 : URI) : void;
	checkExec() : void;
}


declare module Desktop {
	class Action {
		static OPEN : Desktop.Action;
		static EDIT : Desktop.Action;
		static PRINT : Desktop.Action;
		static MAIL : Desktop.Action;
		static BROWSE : Desktop.Action;
	}


	interface Action extends Enum<Desktop.Action> {}}

export = Desktop
