import Object = require('nashorn/java/lang/Object');
import AWTEvent = require('nashorn/java/awt/AWTEvent');
import Enum = require('nashorn/java/lang/Enum');

declare class EventFilter extends Object {
	acceptEvent(arg1 : AWTEvent) : EventFilter.FilterAction;
}


declare module EventFilter {
	class FilterAction {
		static ACCEPT : EventFilter.FilterAction;
		static REJECT : EventFilter.FilterAction;
		static ACCEPT_IMMEDIATELY : EventFilter.FilterAction;
	}


	interface FilterAction extends Enum<EventFilter.FilterAction> {}}

export = EventFilter
