import Object = require('nashorn/java/lang/Object');
import OutputStream = require('nashorn/java/io/OutputStream');
import InputStream = require('nashorn/java/io/InputStream');
import TimeUnit = require('nashorn/java/util/concurrent/TimeUnit');

declare class Process extends Object {
	outputStream : OutputStream;
	inputStream : InputStream;
	errorStream : InputStream;
	alive : boolean;
	constructor();
	getOutputStream() : OutputStream;
	getInputStream() : InputStream;
	getErrorStream() : InputStream;
	waitFor() : number;
	waitFor(arg1 : number, arg2 : TimeUnit) : boolean;
	exitValue() : number;
	destroy() : void;
	destroyForcibly() : Process;
	isAlive() : boolean;
}

export = Process
