import GenericDeclaration = require('nashorn/java/lang/reflect/GenericDeclaration');
import Type = require('nashorn/java/lang/reflect/Type');
import AnnotatedType = require('nashorn/java/lang/reflect/AnnotatedType');
import AnnotatedElement = require('nashorn/java/lang/reflect/AnnotatedElement');

declare class TypeVariable<D extends GenericDeclaration> {
	bounds : Type[];
	genericDeclaration : D;
	name : string;
	annotatedBounds : AnnotatedType[];
	getBounds() : Type[];
	getGenericDeclaration() : D;
	getName() : string;
	getAnnotatedBounds() : AnnotatedType[];
}


declare interface TypeVariable<D extends GenericDeclaration> extends Type, AnnotatedElement {}
export = TypeVariable
