import Object = require('nashorn/java/lang/Object');
import SocketAddress = require('nashorn/java/net/SocketAddress');
import Enum = require('nashorn/java/lang/Enum');

declare class Proxy extends Object {
	static NO_PROXY : Proxy;
	constructor(arg1 : Proxy.Type, arg2 : SocketAddress);
	type() : Proxy.Type;
	address() : SocketAddress;
}


declare module Proxy {
	class Type {
		static DIRECT : Proxy.Type;
		static HTTP : Proxy.Type;
		static SOCKS : Proxy.Type;
	}


	interface Type extends Enum<Proxy.Type> {}}

export = Proxy
