import Object = require('nashorn/java/lang/Object');
import Class = require('nashorn/java/lang/Class');

declare class WatchEvent<T> extends Object {
	kind() : WatchEvent.Kind<T>;
	count() : number;
	context() : T;
}


declare module WatchEvent {
	class Kind<T> extends Object {
		name() : string;
		type() : Class<T>;
	}
	class Modifier extends Object {
		name() : string;
	}
}

export = WatchEvent
