import PublicKey = require('nashorn/java/security/PublicKey');
import Provider = require('nashorn/java/security/Provider');
import Object = require('nashorn/java/lang/Object');
import Serializable = require('nashorn/java/io/Serializable');

declare class Certificate {
	type : string;
	encoded : number[];
	publicKey : PublicKey;
	getType() : string;
	getEncoded() : number[];
	verify(arg1 : PublicKey) : void;
	verify(arg1 : PublicKey, arg2 : string) : void;
	verify(arg1 : PublicKey, arg2 : Provider) : void;
	getPublicKey() : PublicKey;
	writeReplace() : Object;
}


declare interface Certificate extends Serializable {}

declare module Certificate {
	class CertificateRep {
		readResolve() : Object;
	}


	interface CertificateRep extends Serializable {}}

export = Certificate
