import Object = require('nashorn/java/lang/Object');
import TimeUnit = require('nashorn/java/util/concurrent/TimeUnit');

declare class Future<V> extends Object {
	cancelled : boolean;
	done : boolean;
	cancel(arg1 : boolean) : boolean;
	isCancelled() : boolean;
	isDone() : boolean;
	get() : V;
	get(arg1 : number, arg2 : TimeUnit) : V;
}

export = Future
