import Object = require('nashorn/java/lang/Object');
import Thread = require('nashorn/java/lang/Thread');
import Enum = require('nashorn/java/lang/Enum');

declare class TimeUnit {
	static NANOSECONDS : TimeUnit;
	static MICROSECONDS : TimeUnit;
	static MILLISECONDS : TimeUnit;
	static SECONDS : TimeUnit;
	static MINUTES : TimeUnit;
	static HOURS : TimeUnit;
	static DAYS : TimeUnit;
	static x(arg1 : number, arg2 : number, arg3 : number) : number;
	convert(arg1 : number, arg2 : TimeUnit) : number;
	toNanos(arg1 : number) : number;
	toMicros(arg1 : number) : number;
	toMillis(arg1 : number) : number;
	toSeconds(arg1 : number) : number;
	toMinutes(arg1 : number) : number;
	toHours(arg1 : number) : number;
	toDays(arg1 : number) : number;
	excessNanos(arg1 : number, arg2 : number) : number;
	timedWait(arg1 : Object, arg2 : number) : void;
	timedJoin(arg1 : Thread, arg2 : number) : void;
	sleep(arg1 : number) : void;
}


declare interface TimeUnit extends Enum<TimeUnit> {}
export = TimeUnit
