import Object = require('nashorn/java/lang/Object');
import TimeUnit = require('nashorn/java/util/concurrent/TimeUnit');
import Condition = require('nashorn/java/util/concurrent/locks/Condition');

declare class Lock extends Object {
	lock() : void;
	lockInterruptibly() : void;
	tryLock() : boolean;
	tryLock(arg1 : number, arg2 : TimeUnit) : boolean;
	unlock() : void;
	newCondition() : Condition;
}

export = Lock
