import Object = require('nashorn/java/lang/Object');
import Accessible = require('nashorn/javax/accessibility/Accessible');

declare class AccessibleTable extends Object {
	accessibleCaption : Accessible;
	accessibleSummary : Accessible;
	accessibleRowCount : number;
	accessibleColumnCount : number;
	accessibleRowHeader : AccessibleTable;
	accessibleColumnHeader : AccessibleTable;
	selectedAccessibleRows : number[];
	selectedAccessibleColumns : number[];
	getAccessibleCaption() : Accessible;
	setAccessibleCaption(arg1 : Accessible) : void;
	getAccessibleSummary() : Accessible;
	setAccessibleSummary(arg1 : Accessible) : void;
	getAccessibleRowCount() : number;
	getAccessibleColumnCount() : number;
	getAccessibleAt(arg1 : number, arg2 : number) : Accessible;
	getAccessibleRowExtentAt(arg1 : number, arg2 : number) : number;
	getAccessibleColumnExtentAt(arg1 : number, arg2 : number) : number;
	getAccessibleRowHeader() : AccessibleTable;
	setAccessibleRowHeader(arg1 : AccessibleTable) : void;
	getAccessibleColumnHeader() : AccessibleTable;
	setAccessibleColumnHeader(arg1 : AccessibleTable) : void;
	getAccessibleRowDescription(arg1 : number) : Accessible;
	setAccessibleRowDescription(arg1 : number, arg2 : Accessible) : void;
	getAccessibleColumnDescription(arg1 : number) : Accessible;
	setAccessibleColumnDescription(arg1 : number, arg2 : Accessible) : void;
	isAccessibleSelected(arg1 : number, arg2 : number) : boolean;
	isAccessibleRowSelected(arg1 : number) : boolean;
	isAccessibleColumnSelected(arg1 : number) : boolean;
	getSelectedAccessibleRows() : number[];
	getSelectedAccessibleColumns() : number[];
}

export = AccessibleTable
