import Object = require('nashorn/java/lang/Object');
import Point = require('nashorn/java/awt/Point');
import Rectangle = require('nashorn/java/awt/Rectangle');
import AttributeSet = require('nashorn/javax/swing/text/AttributeSet');

declare class AccessibleText extends Object {
	static CHARACTER : number;
	static WORD : number;
	static SENTENCE : number;
	charCount : number;
	caretPosition : number;
	selectionStart : number;
	selectionEnd : number;
	selectedText : string;
	getIndexAtPoint(arg1 : Point) : number;
	getCharacterBounds(arg1 : number) : Rectangle;
	getCharCount() : number;
	getCaretPosition() : number;
	getAtIndex(arg1 : number, arg2 : number) : string;
	getAfterIndex(arg1 : number, arg2 : number) : string;
	getBeforeIndex(arg1 : number, arg2 : number) : string;
	getCharacterAttribute(arg1 : number) : AttributeSet;
	getSelectionStart() : number;
	getSelectionEnd() : number;
	getSelectedText() : string;
}

export = AccessibleText
