import Object = require('nashorn/java/lang/Object');
import Document = require('nashorn/javax/swing/text/Document');
import Element = require('nashorn/javax/swing/text/Element');

declare class DocumentEvent extends Object {
	offset : number;
	length : number;
	document : Document;
	type : DocumentEvent.EventType;
	getOffset() : number;
	getLength() : number;
	getDocument() : Document;
	getType() : DocumentEvent.EventType;
	getChange(arg1 : Element) : DocumentEvent.ElementChange;
}


declare module DocumentEvent {
	class ElementChange extends Object {
		element : Element;
		index : number;
		childrenRemoved : Element[];
		childrenAdded : Element[];
		getElement() : Element;
		getIndex() : number;
		getChildrenRemoved() : Element[];
		getChildrenAdded() : Element[];
	}
	class EventType extends Object {
		static INSERT : DocumentEvent.EventType;
		static REMOVE : DocumentEvent.EventType;
		static CHANGE : DocumentEvent.EventType;
	}
}

export = DocumentEvent
