
declare type Constructor<T> = new (...args: any[])=>T;

declare class Java {
    static isType(type: any): boolean;

    static type(typeName: string): any;

    static typeName(type: any): string;

    static to(obj: any, type: any): any;

    static from(obj: any): any;

    // static extend<T>(clazz: Constructor<T>, ...obj: any[]): Constructor<T>;
    static extend<T>(clazz: T, ...obj: any[]): T;

    static _super(adapter: any): any;
}