import RegExpAtom = require('nashorn/org/intellij/lang/regexp/psi/RegExpAtom');
import RegExpClassElement = require('nashorn/org/intellij/lang/regexp/psi/RegExpClassElement');
import RegExpCharRange = require('nashorn/org/intellij/lang/regexp/psi/RegExpCharRange');
import Enum = require('nashorn/java/lang/Enum');

declare class RegExpChar {
	type : RegExpChar.Type;
	value : string;
	getType() : RegExpChar.Type;
	getValue() : string;
}


declare interface RegExpChar extends RegExpAtom, RegExpClassElement, RegExpCharRange.Endpoint {}

declare module RegExpChar {
	class Type {
		static CHAR : RegExpChar.Type;
		static HEX : RegExpChar.Type;
		static OCT : RegExpChar.Type;
		static UNICODE : RegExpChar.Type;
		static INVALID : RegExpChar.Type;
	}


	interface Type extends Enum<RegExpChar.Type> {}}

export = RegExpChar
