import Node = require('nashorn/org/mozilla/javascript/Node');
import AstNode = require('nashorn/org/mozilla/javascript/ast/AstNode');

declare class Jump {
	target : Node;
	jumpStatement : Jump;
	default : Node;
	finally : Node;
	loop : Jump;
	continue : Node;
	constructor();
	constructor(arg1 : number);
	constructor(arg1 : number, arg2 : number);
	constructor(arg1 : number, arg2 : Node);
	constructor(arg1 : number, arg2 : Node, arg3 : number);
	getJumpStatement() : Jump;
	setJumpStatement(arg1 : Jump) : void;
	getDefault() : Node;
	setDefault(arg1 : Node) : void;
	getFinally() : Node;
	setFinally(arg1 : Node) : void;
	getLoop() : Jump;
	setLoop(arg1 : Jump) : void;
	getContinue() : Node;
	setContinue(arg1 : Node) : void;
}


declare interface Jump extends AstNode {}
export = Jump
