import Element = require('nashorn/org/w3c/dom/Element');
import TypeInfo = require('nashorn/org/w3c/dom/TypeInfo');
import Node = require('nashorn/org/w3c/dom/Node');

declare class Attr {
	name : string;
	specified : boolean;
	value : string;
	ownerElement : Element;
	schemaTypeInfo : TypeInfo;
	id : boolean;
	getName() : string;
	getSpecified() : boolean;
	getValue() : string;
	setValue(arg1 : string) : void;
	getOwnerElement() : Element;
	getSchemaTypeInfo() : TypeInfo;
	isId() : boolean;
}


declare interface Attr extends Node {}
export = Attr
